            }
            else {
                const newCell = <td id={id}></td>
                newTr.push(newCell);
            }

        }
        tBody.push(newTr);
    }
        return (

            <div className="gridContainer">

                <table id="table">
                    <tbody>
                        {
                           tBody.map((tr) => {
                               return <tr>
