import React from 'react';
import ReactDOM from 'react-dom';
// import FontAwesome from '../node_modules/font-awesome/css/font-awesome.min.css'
import './index.css';

class Grid extends React.Component {

    constructor(props) {
        super(props);
        this.handleKeyPress = this.handleKeyPress.bind(this);
        document.addEventListener('keydown', function (event) {
            this.handleKeyPress(event);
        }.bind(this));
        this.state = {
            positionMaleIcon: "i0j0",
            positionKeys: ["i0j3", "i1j2", "i2j0", "i3j1"]
        }
    }
    handleKeyPress = (event) => {

        const pos = this.state.positionMaleIcon;
        let y = parseInt(pos.split('j')[1]);
        let x = parseInt(pos.split('j')[0].split('i')[1]);
        switch(event.key) {
            case "ArrowDown" :
                if( x< 4) {
                    x=x+1;
                }
                break;
            case "ArrowUp" :
                if(x >0 ){
                    x=x-1;
                }
                break;
            case "ArrowLeft" :
                if(y>0){
                    y=y-1;
                }
                break;
            case "ArrowRight" :
                if(y<4){
                    y=y+1;
                }
                break;
        }
        this.setState({ positionMaleIcon : `i${x}j${y}`});
        console.log(this.state.positionMaleIcon);
    };

    render() {
        return (

            <div className="gridContainer">

                <table id="table">
                    <tbody>
                        <tr>
                            <td id="i0j0"> <i className="fa fa-male"></i></td>
                            <td id="i0j1"></td>
                            <td id="i0j2"></td>
                            <td id="i0j3"> <i class="fa fa-key"></i></td>
                        </tr>
                        <tr>
                            <td id="i1j0"> </td>
                            <td id="i1j1"></td>
                            <td id="i1j2"><i class="fa fa-key"></i></td>
                            <td id="i1j3"> </td>
                        </tr>

                        <tr>
                            <td id="i2j0"> <i class="fa fa-key"></i></td>
                            <td id="i2j1"></td>
                            <td id="i2j2"></td>
                            <td id="i2j3"> </td>
                        </tr>

                        <tr>
                            <td id="i3j0"></td>
                            <td id="i3j1"><i class="fa fa-key"></i></td>
                            <td id="i3j2"></td>
                            <td id="i3j3">  </td>
                        </tr>
                    </tbody>
                </table>
            </div>
        );
    }
}
export default Grid;
