    //     }
    //   }
    render() {
        const maleIcon = <i className="fa fa-male"></i>;
        const keyIcon = <i class="fa fa-key"></i>;
        return (

            <div className="gridContainer">

                <table id="table">
                    <tbody>
                        <tr>
                            <td id="i1j1" onKeyDown={this.handleKeyPress} tabindex="1">{maleIcon}</td>
                            <td id="i1j2" tabindex="2"></td>
                            <td id="i1j3" tabindex="3">{keyIcon}</td>
                            <td id="i1j4" tabindex="4"></td>
                        </tr>
                        <tr>
                            <td id="i2j1" tabindex="5"></td>
                            <td id="i2j2" tabindex="6">{keyIcon}</td>
                            <td id="i2j3" tabindex="7"></td>
                            <td id="i2j4" tabindex="8"></td>
                        </tr>
                        <tr>
                            <td id="i3j1"tabindex="9"></td>
                            <td id="i3j2"tabindex="10"></td>
                            <td id="i3j3"tabindex="11"></td>
                            <td id="i3j4"tabindex="12">{keyIcon}</td>
                        </tr>
                        <tr>
                            <td id="i4j1"tabindex="13">{keyIcon}</td>
                            <td id="i4j2"tabindex="14"></td>
                            <td id="i4j3"tabindex="15"></td>
                            <td id="i4j4"tabindex="16"></td>
                        </tr>
                    </tbody>
                </table>
            </div>
        );
    }
}
