import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Grid extends React.Component {
    // constructor(props){
    //     super(props);
    //     this.col=4;
    //     this.current;
    //     const next;
    //     // var table = document.getElementsByClassName("mstrTable");
    //     // var tbody = table.getElementsByTagName("tbody")[0];
    //     // var ishigh;

    // }

    handleKeyPress = e=> {
     var col=4;
     var current,next;
        var code = e.keyCode;
        switch(code){
            case 37: next = current - 1

            alert(next); break; 		//left
			case 38: next = current - col; break; 		//up
			case 39: next = (1*current) + 1; break; 	//right
			case 40: next = (1*current) + col; break; 
        }

    };


    // handleKeyPress = (event) => {
    //     if(event.key == 'ArrowDown'){
    //       alert('enter press here! ')
    //     }
    //   }
    render() {
        return(

            <div className="gridContainer">

         <table>
             <tbody>
                 <tr>
                     <td onKeyDown={this.handleKeyPress} tabindex="1"><i className="fa fa-male"></i></td>
                     <td tabindex="2"></td>
                     <td tabindex="3"><i class="fa fa-key"></i></td>
                     <td tabindex="4"></td>
                 </tr>
                 <tr>
                     <td tabindex="5"></td>
                     <td tabindex="6"><i class="fa fa-key"></i></td>
                     <td tabindex="7"></td>
                     <td tabindex="8"></td>
                 </tr>
                 <tr>
                     <td tabindex="9"></td>
                     <td tabindex="10"></td>
                     <td tabindex="11"></td>
                     <td tabindex="12"><i class="fa fa-key"></i></td>
                 </tr>
                 <tr>
                     <td tabindex="13"><i class="fa fa-key"></i></td>
                     <td tabindex="14"></td>
                     <td tabindex="15"></td>
                     <td tabindex="16"></td>
                 </tr>
             </tbody>
         </table>
            </div>
        );
    }
}
export default Grid;
