import React from 'react';
import ReactDOM from 'react-dom';
// import FontAwesome from '../node_modules/font-awesome/css/font-awesome.min.css'
import './index.css';

class Grid extends React.Component {
    state = {
        maleIcon: <i className="fa fa-male"></i>,
        keyIcon: <i class="fa fa-key"></i>
    }

    handleKeyPress = e => {
        var col = 4;
        var current, next;
        if (e.key == 'ArrowDown') {
            next = (1 * current) + col;
            alert(next);
        }
        else if (e.key == 'ArrowUp') {
            next = current - 1;
            alert(next);
        }
        // var code = e.keyCode;
        // switch(code){
        //     case 37: next = current - 1; break; 		//left
        // 	case 38: next = current - col; break; 		//up
        // 	case 39: next = (1*current) + 1; break; 	//right
        // 	case 40: next = (1*current) + col; break; 
        // }

    };


    // handleKeyPress = (event) => {
    //     if(event.key == 'ArrowDown'){
    //       alert('enter press here! ')
    //     }
    //   }
    render() {
        return (

            <div className="gridContainer">

                <table>
                    <tbody>
                        <tr>
                            <td onKeyDown={this.handleKeyPress} tabindex="1">{this.state.maleIcon}</td>
                            <td tabindex="2"></td>
                            <td tabindex="3">{this.state.keyIcon}</td>
                            <td tabindex="4"></td>
                        </tr>
                        <tr>
                            <td tabindex="5"></td>
                            <td tabindex="6">{this.state.keyIcon}</td>
                            <td tabindex="7"></td>
                            <td tabindex="8"></td>
                        </tr>
                        <tr>
                            <td tabindex="9"></td>
                            <td tabindex="10"></td>
                            <td tabindex="11"></td>
                            <td tabindex="12">{this.state.keyIcon}</td>
                        </tr>
                        <tr>
                            <td tabindex="13">{this.state.keyIcon}</td>
                            <td tabindex="14"></td>
                            <td tabindex="15"></td>
                            <td tabindex="16"></td>
                        </tr>
                    </tbody>
                </table>
            </div>
        );
    }
}
export default Grid;
