import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Grid extends React.Component {
    start = document.getElementsByClassName('fa-male');
//     start.focus();
// start.style.backgroundColor = 'green';
// document.onkeydown = checkKey;
dotheneedful=sibling => {
    if (sibling != null) {
      start.focus();
      start.style.backgroundColor = '';
      start.style.color = '';
      sibling.focus();
      sibling.style.backgroundColor = 'green';
      sibling.style.color = 'white';
      start = sibling;
    }
  }
checkKey = e => {
  if (e.keyCode == '38') {
    // up arrow
    var idx = this.start.cellIndex;
    var nextrow = this.start.parentElement.previousElementSibling;
    if (nextrow != null) {
      var sibling = nextrow.cells[idx];
      dotheneedful(sibling);
    }
  } else if (e.keyCode == '40') {
    // down arrow
    var idx = start.cellIndex;
    var nextrow = start.parentElement.nextElementSibling;
    if (nextrow != null) {
      var sibling = nextrow.cells[idx];
      dotheneedful(sibling);
    }
  } else if (e.keyCode == '37') {
    // left arrow
    var sibling = start.previousElementSibling;
    dotheneedful(sibling);
  } else if (e.keyCode == '39') {
    // right arrow
    var sibling = start.nextElementSibling;
    dotheneedful(sibling);
  }
}
    keyArrow = (event) => {
        if(event.key == 'ArrowDown'){
          alert('enter press here! ')
        }
      }
    render() {
        return(

            <div className="gridContainer">
             {/* <div>
            <input type="text" id="one" onKeyDown={this.handleKeyPress} />
         </div> */}
         <table>
             <tbody>
                 <tr>
                     <td onKeyDown={this.checkKey}><i className="fa fa-male"></i></td>
                     <td></td>
                     <td><i class="fa fa-key"></i></td>
                     <td></td>
                 </tr>
                 <tr>
                     <td></td>
                     <td><i class="fa fa-key"></i></td>
                     <td></td>
                     <td></td>
                 </tr>
                 <tr>
                     <td></td>
                     <td></td>
                     <td></td>
                     <td><i class="fa fa-key"></i></td>
                 </tr>
                 <tr>
                     <td><i class="fa fa-key"></i></td>
                     <td></td>
                     <td></td>
                     <td></td>
                 </tr>
             </tbody>
         </table>











{/*          
                <div className="gridItem" onKeyDown={this.keyArrow}><i className="fa fa-male"></i></div>
                <div className="gridItem"></div>
                <div className="gridItem"><i class="fa fa-key"></i></div>
                <div className="gridItem"></div>
                <div className="gridItem"></div>
                <div className="gridItem"></div>
                <div className="gridItem"></div>
                <div className="gridItem"></div>
                <div className="gridItem"></div>
                <div className="gridItem"><i class="fa fa-key"></i></div>
                <div className="gridItem"></div>
                <div className="gridItem"></div>
                <div className="gridItem"></div>
                <div className="gridItem"></div>
                <div className="gridItem"></div>
                <div className="gridItem"><i class="fa fa-key"></i></div> */}
            </div>
        );
    }
}
export default Grid;
