import React from 'react';
import ReactDOM from 'react-dom';
// import FontAwesome from '../node_modules/font-awesome/css/font-awesome.min.css'
import './index.css';

class Grid extends React.Component {

//     handleKeyPress = e => {
// if (e.key == 'ArrowDown') {
//         alert("hi");
//     myTab.document.getElement
//      j=j++;

//     myTab.cells[j]=this.setState({positionMaleIcon:"i1j1"})
//     for (const i = 0; i < myTab.rows.length - 1; i++) {
//      for (const j = 0; j < myTab.cells.length;j++) {   
//  alert(myTab.rows[i][j]);
//     if(this.state.positionKeys == "")
//       this.setState({positionMaleIcon:"i2j1"});

//        }
//      };

    constructor(props) {
        super(props);
        this.handleKeyPress= this.handleKeyPress.bind(this);
        document.addEventListener('keydown', function(event) {
            this.handleKeyPress(event);
        }.bind(this));
        this.state = {
            positionMaleIcon :"i0j0",
            positionKeys : ["i0j3", "i2j3","i1j1", "i3j0"]

            // maleIcon: <i className="fa fa-male"></i>,
            // keyIcon: <i class="fa fa-key"></i>
        }
    }
    handleKeyPress = (event) => {
        if(event.key == 'ArrowDown'){

            j=j++;
        //   alert('enter press here! ')
        }
    };

    render() {
        const tBody = [];

    const maleIcon = <i className="fa fa-male"></i>;
    const keyIcon = <i class="fa fa-key"></i>;
    for ( let i =0 ; i<4; i++ ){
        const newTr=[];
        for ( let j=0; j<4; j++){
            const id = `i${i}j${j}`;

            if(id == this.state.positionMaleIcon)
            {
                const newCell = <td id={id} >{maleIcon}</td>
                newTr.push(newCell);
            }
            else if(this.state.positionKeys.includes(id)) {
                const newCell = <td id={id}>{keyIcon}</td>
                newTr.push(newCell);
            }
            else {
                const newCell = <td id={id}></td>
                newTr.push(newCell);
            }

        }
        tBody.push(newTr);
    }
        return (

            <div className="gridContainer">

                <table id="table">
                    <tbody>
                        {
                           tBody.map((tr) => {
                               return <tr>
                                   {
                                   tr.map(cell => <td tabindex="1">{cell}</td>)
                                   }
                               </tr>
                           }) 
                        }
                    </tbody>
                </table>
            </div>
        );
    }
}
export default Grid;
